import React, { Component } from "react";
import FighterService from "../services/fighter.service";
import CoachService from "../services/coach.service";

export default class FighterCard extends Component {
    

    componentDidMount() {
        var x;

        if (localStorage.getItem("userRole")==="ROLE_COACH") {
            CoachService.getCoachId(localStorage.getItem("username")).then((response)=> {
                localStorage.setItem("coachid",response.data)
            });
            FighterService.hasCoach(props.id).then((response) => {
                x = response.data;
                console.log(x);
            })
        }
    }
    render() {
        return (
            <div className="category-card">
                {
                     localStorage.getItem("userRole")==="ROLE_ADMIN" && (
                        <button id = "delete-button" onClick = {() =>
                            [FighterService.deleteFighter(props.id), window.location.reload()]} > X </button> )
                }
                {
                    
                    localStorage.getItem("userRole")==="ROLE_COACH" && x===false && (
                        <button id = "delete-button" onClick = {() =>
                            [CoachService.sendRequest(localStorage.getItem("coachid"), props.id)]} > Send </button> )
                }
                            <a onClick= {()=> {window.location.href = "fighter?id="+ props.id}}>
                                <div className="category-card-image">
                                <img className="card-image" alt="fighter-image" src={`data:image/jpg;base64,${props.imageBinary}`} width='250px' height = '200px'/>
                                </div>
                                <div className="category-card-text">
                                    <p>{props.fighterName}</p>
                                </div>
                            </a>    
                     
                
            </div>
        );
    }
    
}
