import React, { Component } from "react";
import { Switch, Route, Link} from "react-router-dom";
import "./Navbar.css";

import Login from "./login.component";
import Register from "./register.component";
import Home from "./home.component";
import Profile from "./profile.component";
import FightsComponent from "./fights.component";
import UsersComponent from "./users.component";
import WeightClassesComponent from "./weightclasses.component";
import FightResultComponent from "./fight.result.component";
import AuthService from "../services/auth.service";
import FightComponent from "./fight.component";
import Chat from "./chat.component";
import WeightclassComponent from "./weightclass.component";
import FighterComponent from "./fighter.component";
import CreateFight from "./create-fight.component";
import SetImageComponent from "./set-fight-image.component";
import CreateFighter from "./create-fighter.component";
import SetFighterImageComponent from "./set-fighter-image.component";
import Fighters from "./fighters.component";
import MyFights from "./my-fights.component";


class Navbar extends Component {
    constructor(props) {
        super(props);
        this.logOut = this.logOut.bind(this);
    
        this.state = {
          currentUser: undefined,
        };
      }
      componentDidMount() {
        const user = AuthService.getCurrentUser();
    
        if (user) {
          this.setState({
            currentUser: user,
          });
        }
        
      }
      
      logOut() {
        AuthService.logout();
      }
     
    render() {

        const { currentUser } = this.state;
        let roleName = "";
        if (localStorage.getItem("userRole")) {
           roleName = localStorage.getItem("userRole");
        } else {
          roleName = "Nothing";
        }
       
        console.log(roleName);
        return (
            <div>
        <nav className="navbar navbar-expand navbar-dark bg-dark">
          <Link to={"/"} className="navbar-brand">
            BoksTime
          </Link>
          <div className="navbar-nav mr-auto">
              {!currentUser && (
            <li className="nav-item">
              <Link to={"/home"} className="nav-link">
                Home
              </Link>
            </li>
              )}


            {roleName === "ROLE_ADMIN" && (
              <li className="nav-item">
                <Link to={"/users"} className="nav-link">
                  Users
                </Link>
              </li>
            )}

            {currentUser && roleName !== "ROLE_BOXER" && (
              <li className="nav-item">
                <Link to={"/fights"} className="nav-link">
                  Fights
                </Link>
              </li>
            )}
            {currentUser && roleName !== "ROLE_ADMIN" && (
              <li className="nav-item">
                <Link to={"/chat"} className="nav-link">
                  Chat
                </Link>
              </li>
            )}

            {currentUser && roleName !== "ROLE_ADMIN" && roleName !== "ROLE_BOXER" && (
              <li className="nav-item">
                <Link to={"/weight-classes"} className="nav-link">
                  Weight Classes
                </Link>
              </li>
            )}
            {currentUser && roleName === "ROLE_ADMIN" && (
              <li className="nav-item">
                <Link to={"/fighters"} className="nav-link">
                  Fighters
                </Link>
              </li>
            )}
            {currentUser && roleName === "ROLE_ADMIN" && (
              <li className="nav-item">
                <Link to={"/create-fight"} className="nav-link">
                  Create Fight
                </Link>
              </li>
            )}
            {currentUser && roleName === "ROLE_ADMIN" && (
              <li className="nav-item">
                <Link to={"/create-fighter"} className="nav-link">
                  Create Fighter
                </Link>
              </li>
            )}
            {currentUser && roleName === "ROLE_BOXER" && (
              <li className="nav-item">
                <Link to={"/my-fights"} className="nav-link">
                  My Fights
                </Link>
              </li>
            )}
            {currentUser && roleName === "ROLE_BOXER" && (
              <li className="nav-item">
                <Link to={"/statistics"} className="nav-link">
                  Statistics
                </Link>
              </li>
            )}
          </div>

          {currentUser ? (
            <div className="navbar-nav ml-auto">
              <li className="nav-item">
                <Link to={"/profile"} className="nav-link">
                  Profile
                </Link>
              </li>
              <li className="nav-item">
                <a href="/login" className="nav-link" id="logout-button" onClick={this.logOut}>
                  LogOut
                </a>
              </li>
            </div>
          ) : (
            <div className="navbar-nav ml-auto">
              <li className="nav-item">
                <Link to={"/login"} className="nav-link">
                  Login
                </Link>
              </li>

              <li className="nav-item">
                <Link to={"/register"} className="nav-link">
                  Sign Up
                </Link>
              </li>
            </div>
          )}
        </nav>

        <div className="container mt-3">
            <Switch>
              <Route exact path={["/", "/home"]} component={Home} />
              <Route exact path="/login" component={Login}/>
              <Route exact path="/register" component={Register}/>
              <Route exact path="/profile" component={Profile}/>
              <Route exact path="/users" component={UsersComponent} />
              <Route exact path="/fights" component={FightsComponent} />
              <Route exact path="/fight-result" component={FightResultComponent} />
              <Route exact path="/fight" component={FightComponent} />
              <Route exact path="/weight-classes" component={WeightClassesComponent} />
              <Route exact path="/chat" component={Chat} />
              <Route exact path="/weight-class" component={WeightclassComponent} />
              <Route exact path="/fighter" component={FighterComponent} />
              <Route exact path="/create-fight" component={CreateFight} />
              <Route exact path="/set-image" component={SetImageComponent} />
              <Route exact path="/create-fighter" component={CreateFighter} />
              <Route exact path="/set-fighter-image" component={SetFighterImageComponent} />
              <Route exact path="/fighters" component={Fighters} />
              <Route exact path="/my-fights" component={MyFights} />

            </Switch>
        </div>
      </div>
        );

    }
    
    
}
export default Navbar;
